#!/bin/bash# F1 Quantum-Aero Platform - Complete Demo Startup Script# Launches all services for real-time demonstrationecho "=================================="echo "F1 Quantum-Aero Platform"echo "Real-Time Demo Startup"echo "=================================="echo ""# ColorsGREEN='\033[0;32m'BLUE='\033[0;34m'YELLOW='\033[1;33m'NC='\033[0m' # No Color# Check if Python is availableif ! command -v python3 &> /dev/null; then    echo -e "${YELLOW}ÔÜá´©Å  Python3 not found. Please install Python 3.9+${NC}"    exit 1fi# Check if Node.js is availableif ! command -v node &> /dev/null; then    echo -e "${YELLOW}ÔÜá´©Å  Node.js not found. Please install Node.js 16+${NC}"    exit 1fiecho -e "${BLUE}­ƒôª Installing dependencies...${NC}"echo ""# Install Python dependencies (if needed)if [ ! -d "venv" ]; then    echo "Creating virtual environment..."    python3 -m venv venvfisource venv/bin/activatepip install -q fastapi uvicorn websockets numpy 2>/dev/null || echo "Python packages already installed"# Install frontend dependencies (if needed)if [ ! -d "frontend/node_modules" ]; then    echo "Installing frontend dependencies..."    cd frontend && npm install --silent && cd ..fiecho ""echo -e "${GREEN}Ô£ô Dependencies ready${NC}"echo ""# Create logs directorymkdir -p logsecho -e "${BLUE}­ƒÜÇ Starting services...${NC}"echo ""# Start Real-Time WebSocket Serverecho -e "${GREEN}[1/2]${NC} Starting Real-Time Server (Port 8010)..."python3 realtime_server.py > logs/realtime_server.log 2>&1 &REALTIME_PID=$!sleep 2# Check if server startedif ps -p $REALTIME_PID > /dev/null; then    echo -e "${GREEN}  Ô£ô Real-Time Server running (PID: $REALTIME_PID)${NC}"else    echo -e "${YELLOW}  ÔÜá´©Å  Real-Time Server failed to start. Check logs/realtime_server.log${NC}"fi# Start Frontendecho -e "${GREEN}[2/2]${NC} Starting Frontend Dashboard (Port 3000)..."cd frontendnpm start > ../logs/frontend.log 2>&1 &FRONTEND_PID=$!cd ..sleep 3echo ""echo "=================================="echo -e "${GREEN}Ô£ô All services started!${NC}"echo "=================================="echo ""echo "Access the platform:"echo ""echo -e "  ${BLUE}Frontend Dashboard:${NC}  http://localhost:3000"echo -e "  ${BLUE}Real-Time Server:${NC}   http://localhost:8010"echo -e "  ${BLUE}WebSocket:${NC}          ws://localhost:8010/ws/quantum"echo ""echo "=================================="echo ""echo "Process IDs:"echo "  Real-Time Server: $REALTIME_PID"echo "  Frontend:         $FRONTEND_PID"echo ""echo "To stop all services:"echo "  kill $REALTIME_PID $FRONTEND_PID"echo ""echo "Or use: ./stop_demo.sh"echo ""echo "Logs available in: ./logs/"echo ""echo -e "${GREEN}­ƒÄë Ready for demonstration!${NC}"echo ""echo "Open http://localhost:3000 and click 'Real-Time Simulation' tab"echo ""# Save PIDs for stop scriptecho "$REALTIME_PID" > logs/realtime.pidecho "$FRONTEND_PID" > logs/frontend.pid# Keep script runningecho "Press Ctrl+C to stop all services..."wait