#!/bin/bash# F1 Quantum-Aero Platform - Stop Demo Scriptecho "=================================="echo "Stopping F1 Quantum-Aero Platform"echo "=================================="echo ""# ColorsGREEN='\033[0;32m'RED='\033[0;31m'NC='\033[0m' # No Color# Read PIDsif [ -f "logs/realtime.pid" ]; then    REALTIME_PID=$(cat logs/realtime.pid)    if ps -p $REALTIME_PID > /dev/null 2>&1; then        echo -e "Stopping Real-Time Server (PID: $REALTIME_PID)..."        kill $REALTIME_PID 2>/dev/null        echo -e "${GREEN}Ô£ô Real-Time Server stopped${NC}"    fi    rm logs/realtime.pidfiif [ -f "logs/frontend.pid" ]; then    FRONTEND_PID=$(cat logs/frontend.pid)    if ps -p $FRONTEND_PID > /dev/null 2>&1; then        echo -e "Stopping Frontend (PID: $FRONTEND_PID)..."        kill $FRONTEND_PID 2>/dev/null        echo -e "${GREEN}Ô£ô Frontend stopped${NC}"    fi    rm logs/frontend.pidfi# Kill any remaining processes on portsecho ""echo "Cleaning up ports..."lsof -ti:8010 | xargs kill -9 2>/dev/null && echo -e "${GREEN}Ô£ô Port 8010 freed${NC}"lsof -ti:3000 | xargs kill -9 2>/dev/null && echo -e "${GREEN}Ô£ô Port 3000 freed${NC}"echo ""echo -e "${GREEN}Ô£ô All services stopped${NC}"echo ""